import React, { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";

const monday = mondaySdk();

const App = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const query = `
    query {
      me {
        name
      }
      boards(ids: 6715436980) {
        name
        columns {
          title
          id
          type
        }
        groups {
          title
          id
        }
        items_page (query_params: {order_by:[{column_id:"name"}]}) {
          cursor
          items {
            id 
            name 

            column_values{           
              text             
            }
          }
        }
      }
    }`;

    fetch("https://api.monday.com/v2", {
      method: "post",
      headers: {
        "Content-Type": "application/json",
        Authorization:
          "eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjM2NjA0MDUyNiwiYWFpIjoxMSwidWlkIjo2MTQyNzk4MywiaWFkIjoiMjAyNC0wNS0zMFQxODo0NjoxOS4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTY2NTczMDMsInJnbiI6InVzZTEifQ.FR7Ta7rJp6dRtumR2cNFHTVHMLfiDognJMEJEUE8Xdo",
      },
      body: JSON.stringify({
        query: query,
      }),
    })
      .then((res) => res.json())
      .then((res) => {
        setData(res.data);
      });
  }, []);

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Nombre del Usuario: {data.me.name}</h1>
      <h2>Nombre del Tablero: {data.boards[0].name}</h2>
      <h3>Columnas:</h3>
      <ul>
        {data.boards[0].columns.map((column) => (
          <li key={column.id}>
            {column.title} (Tipo: {column.type})
          </li>
        ))}
      </ul>
      <h3>Grupos:</h3>
      <ul>
        {data.boards[0].groups.map((group) => (
          <li key={group.id}>{group.title}</li>
        ))}
      </ul>

      <table>
        <thead>
        <tr>
          <th>Nombre</th>
          <th>Jefe directo</th>
          <th>Fecha de ingreso</th>
          <th>Id del elemento</th>
        </tr>

        </thead>

        <tbody>
          <tr>
          {data.boards[0].items_page.items.map((item) => (
          <th key={item.id}>{item.name}</th>
        ))}</tr>
        {data.boards[0].items_page.items.map((item_v) => (
          <td>
            {item_v.column_values.map((value, index) =>(<td key={index}>{value.text}</td>) )}
          </td>
        ))}


        </tbody>



      </table>
    </div>
  );
};
export default App;
