import React, { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";

const monday = mondaySdk();

const App = () => {
  const [items, setItems] = useState([]);
  const [userId, setUserId] = useState(null);
  const [contextData, setContextData] = useState(null);
  const [itemIds, setItemIds] = useState([]);
  const [eventData, setEventData] = useState(null);

  useEffect(() => {
    // Fetch board items
    const query = `query { board (id: BOARD_ID) { items { id name column_values } } }`;
    monday.api(query).then(res => {
      setItems(res.data.board.items);
    });

    // Fetch context data
    monday.get("context").then(res => {
      setUserId(res.data.user.id);
      setContextData(res.data);
    });

    // Fetch visible item IDs
    monday.get("itemIds").then(res => {
      setItemIds(res.data.itemIds);
    });

    // Listen for events
    monday.listen("events", res => {
      setEventData(res.data);
    });
  }, []);

  return (
    <div className="App">
      <AttentionBox
        title="Hello Monday Apps!"
        text={`User ID: ${userId}`}
        type="success"
      />
      <div>
        <h2>Context Data</h2>
        <pre>{JSON.stringify(contextData, null, 2)}</pre>
      </div>
      <div>
        <h2>Visible Item IDs</h2>
        <pre>{JSON.stringify(itemIds, null, 2)}</pre>
      </div>
      <div>
        <h2>Event Data</h2>
        <pre>{JSON.stringify(eventData, null, 2)}</pre>
      </div>
      {/* Render the items */}
      <div>
        {items.map(item => (
          <div key={item.id}>
            <h3>{item.name}</h3>
            <ul>
              {item.column_values.map(column => (
                <li key={column.id}>{column.title}: {column.text}</li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
