import React, { useState, useEffect } from "react";
import "./App.css";
import mondaySdk from "monday-sdk-js";
import "monday-ui-react-core/dist/main.css";
import AttentionBox from "monday-ui-react-core/dist/AttentionBox.js";

const monday = mondaySdk();

const App = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    const query = `
    query {
      me {
        name
      }
      boards(ids: 6715436980) {
        name
        columns {
          title
          id
          type
        }
        groups {
          title
          id
        }
        items_page (query_params: {order_by:[{column_id:"name"}]}) {
          cursor
          items {
            id 
            name 

            column_values{


              __typename
              text
              id

            }


          }

        }

      }
    }`;

    fetch("https://api.monday.com/v2", {
      method: 'post',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'eyJhbGciOiJIUzI1NiJ9.eyJ0aWQiOjM2NjA0MDUyNiwiYWFpIjoxMSwidWlkIjo2MTQyNzk4MywiaWFkIjoiMjAyNC0wNS0zMFQxODo0NjoxOS4wMDBaIiwicGVyIjoibWU6d3JpdGUiLCJhY3RpZCI6MTY2NTczMDMsInJnbiI6InVzZTEifQ.FR7Ta7rJp6dRtumR2cNFHTVHMLfiDognJMEJEUE8Xdo'
      },
      body: JSON.stringify({
        'query': query
      })
    })
      .then(res => res.json())
      .then(res => {
        setData(res.data);
      });
  }, []);

  if (!data) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>Nombre del Usuario: {data.me.name}</h1>
      <h2>Nombre del Tablero: {data.boards[0].name}</h2>
      <h3>Columnas:</h3>
      <ul>
        {data.boards[0].columns.map(column => (
          <li key={column.id}>
            {column.title} (Tipo: {column.type})
          </li>
        ))}
      </ul>
      <h3>Grupos:</h3>
      <ul>
        {data.boards[0].groups.map(group => (
          <li key={group.id}>{group.title}</li>
        ))}
      </ul>


      <table>
        <p>
          tablas
        </p>
        <tr>
          <td>Nombre</td>
          <td>Jefe directo</td>
          <td>Fecha de ingreso</td>
          <td>Id del elemento</td>

        </tr>
        <tr>
          {data.boards[0].items_page.items.map(item =>(
            <td key={item.id}>{item.name}</td>
          ))}
          <td>Jack Russell</td>
          <td>Caniche</td>
          <td>Perro callejero</td>
        </tr>

        <tr>
          <td>HÃ¡bitos alimentarios</td>
          <td>Come las sobras de todos</td>

          <td>Mordisquea la comida</td>
          <td>Come en abundancia</td>

        </tr>
      </table>





    </div>
  );
};
export default App;
